import { Children, useCallback, useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function handleShowAddFriend() {}

export default function App() {
  const [showAddFriend, setShowAddFriend] = useState(false);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList />
        {showAddFriend && <Form />}
        <Button onClick={handleShowAddFriend}>Add Friend</Button>
      </div>
      <BillForm />
    </div>
  );
}

/// 1  Put onClick on the button in the App tree - followed by a function for it
/// 2 accept it as an arguement
/// 3 on that compoenet - pass that onClick in
/// 4 make function = this will change the state to opposite

function FriendsList() {
  const friends = initialFriends;

  return (
    <ul>
      {friends.map((friend) => (
        <Friend friend={friend} />
      ))}
    </ul>
  );
}

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>

      {friend.balance < 0 && (
        <p className="red">You owe £{Math.abs(friend.balance)}</p>
      )}
      {friend.balance > 0 && (
        <p className="green">
          {friend.name} owes you £{friend.balance}
        </p>
      )}
      {friend.balance === 0 && <p>You and {friend.name} are even</p>}

      <Button>Select</Button>
    </li>
  );
}

function Form() {
  return (
    <form className="form-add-friend">
      <label>👭Friend name</label>
      <input></input>

      <label>🎆Image URL</label>
      <input></input>

      <Button>Add</Button>
    </form>
  );
}

function BillForm() {
  return (
    <form className="form-split-bill">
      <h2>Split the bill with X</h2>

      <label>🤑Bill Value</label>
      <input type="text"></input>

      <label>🧍Your expense</label>
      <input type="text"></input>

      <label>👭X's expense</label>
      <input type="text" disabled></input>

      <label>💰Who's paying the bill?</label>

      <select>
        <option value="user">You</option>
        <option value="friend">X</option>
      </select>

      <Button>Split bill</Button>
    </form>
  );
}
