import { Children, useCallback, useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [formOpen, setFormOpen] = useState(false);
  const [friends, setFriends] = useState(initialFriends);

  ///// STATES ^^

  //////////////////// FUNCTIONS

  function handleForm() {
    setFormOpen((form) => !form);
  }

  ///////////////////////

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList />
        {formOpen && <InputForm />}
        <Button handleForm={handleForm}>{formOpen ? "Close" : "Open"}</Button>
      </div>

      <BillForm />
    </div>
  );
}

function FriendsList({ friends }) {
  return (
    <ul>
      {friends.map((friend) => (
        <Friends friends={friend} key={friend.name} />
      ))}
    </ul>
  );
}

function Friends({ friends }) {
  return (
    <li>
      <img src={friends.image} alt={friends.name} />
      <h3>{friends.name}</h3>

      {friends.balance < 0 && (
        <p className="red">
          You owe {friends.name} £{friends.balance}
        </p>
      )}
      {friends.balance > 0 && (
        <p className="green">
          {friends.name} owes you £{friends.balance}
        </p>
      )}

      {friends.balance === 0 && <p>You and {friends.name} are even</p>}
    </li>
  );
}

function InputForm() {
  const [name, setName] = useState("");
  const [image, setImage] = useState("");

  function handleForm(e) {
    e.preventDefault(e);

    const id = crypto.randomUUID();

    const newFriend = {
      name,
      image,
      balance: 0,
      id,
    };

    console.log(newFriend);

    setName("");
    setImage("");
  }

  return (
    <form className="form-add-friend" onSubmit={handleForm}>
      <label>Friend Name</label>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <label>Image URL</label>
      <input
        type="text"
        value={image}
        onChange={(e) => setImage(e.target.value)}
      />
      <Button>Add</Button>
    </form>
  );
}

function Button({ children, handleForm }) {
  return (
    <button className="button" onClick={handleForm}>
      {children}
    </button>
  );
}

function BillForm() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with x</h2>

      <label>Bill Value</label>
      <input type="text" />

      <label>Your expense</label>
      <input type="text" />

      <label>Sarah's expense</label>
      <input type="text" />

      <label>Who is paying the bill</label>

      <select>
        <option>You</option>
        <option>Friend</option>
      </select>
    </form>
  );
}
